import React, { useState, useEffect } from 'react';
import { productsAPI } from '../api';
import CategoryFilter from './CategoryFilter';

const ProductList = ({ user }) => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');
  const [sortBy, setSortBy] = useState('-created_at');

  useEffect(() => {
    fetchProducts();
    fetchCategories();
  }, [searchTerm, selectedCategory, sortBy]);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const params = {
        search: searchTerm,
        category: selectedCategory,
        ordering: sortBy,
      };
      const response = await productsAPI.getProducts(params);
      setProducts(response.data.results || response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await productsAPI.getCategories();
      setCategories(response.data);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center py-8">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-eco-green"></div>
      </div>
    );
  }

  return (
    <div>
      {/* Search and Filter Bar */}
      <div className="mb-6 bg-white p-4 rounded-lg shadow">
        <div className="flex flex-col md:flex-row gap-4">
          <input
            type="text"
            placeholder="Search products..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="flex-1 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-eco-green focus:border-transparent"
          />
          
          <select
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
            className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-eco-green focus:border-transparent"
          >
            <option value="">All Categories</option>
            {categories.map(category => (
              <option key={category.id} value={category.id}>
                {category.name}
              </option>
            ))}
          </select>
          
          <select
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
            className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-eco-green focus:border-transparent"
          >
            <option value="-created_at">Newest First</option>
            <option value="created_at">Oldest First</option>
            <option value="price">Price: Low to High</option>
            <option value="-price">Price: High to Low</option>
          </select>
        </div>
      </div>

      {/* Products Grid */}
      {products.length === 0 ? (
        <div className="text-center py-12">
          <div className="text-6xl mb-4">üîç</div>
          <h3 className="text-xl font-semibold mb-2 text-gray-700">No products found</h3>
          <p className="text-gray-500">Try adjusting your search or browse different categories</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {products.map(product => {
            // Calculate simulated discount
            const originalPrice = (product.price * (1 + Math.random() * 0.5 + 0.2)).toFixed(2);
            const discount = Math.round(((originalPrice - product.price) / originalPrice) * 100);
            
            // Condition badge styling
            const getConditionBadge = (condition) => {
              const badges = {
                'excellent': 'bg-emerald-100 text-emerald-800 border-emerald-200',
                'good': 'bg-blue-100 text-blue-800 border-blue-200',
                'fair': 'bg-yellow-100 text-yellow-800 border-yellow-200',
                'poor': 'bg-red-100 text-red-800 border-red-200'
              };
              return badges[condition] || badges['good'];
            };

            return (
              <div key={product.id} className="bg-white rounded-xl shadow-lg overflow-hidden hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1">
                {/* Image Container */}
                <div className="relative">
                  {product.image_url ? (
                    <img
                      src={product.image_url}
                      alt={product.title}
                      className="w-full h-48 object-cover"
                      onError={(e) => {
                        e.target.src = 'https://images.unsplash.com/photo-1560472354-b33ff0c44a43?w=500&h=400&fit=crop';
                      }}
                    />
                  ) : (
                    <div className="w-full h-48 bg-gray-200 flex items-center justify-center">
                      <span className="text-gray-400 text-4xl">üì¶</span>
                    </div>
                  )}
                  
                  {/* Eco Badge */}
                  <div className="absolute top-3 left-3">
                    <span className="bg-eco-green text-white px-2 py-1 rounded-full text-xs font-medium">
                      Eco {85 + Math.floor(Math.random() * 15)}%
                    </span>
                  </div>
                  
                  {/* Discount Badge */}
                  {discount > 10 && (
                    <div className="absolute top-3 right-12">
                      <span className="bg-red-500 text-white px-2 py-1 rounded-full text-xs font-medium">
                        {discount}% off
                      </span>
                    </div>
                  )}
                  
                  {/* Heart Icon */}
                  <button className="absolute top-3 right-3 w-8 h-8 bg-white rounded-full flex items-center justify-center shadow-md hover:bg-gray-50 transition-colors">
                    <span className="text-gray-400 hover:text-red-500 transition-colors">‚ô°</span>
                  </button>
                </div>

                {/* Content */}
                <div className="p-4">
                  <h3 className="text-lg font-semibold text-gray-900 mb-2 line-clamp-2">{product.title}</h3>
                  
                  {/* Price Section */}
                  <div className="flex items-center justify-between mb-2">
                    <div className="flex items-center space-x-2">
                      <span className="text-2xl font-bold text-gray-900">${product.price}</span>
                      {discount > 10 && (
                        <span className="text-sm text-gray-500 line-through">${originalPrice}</span>
                      )}
                    </div>
                    <div className={`px-2 py-1 rounded-full text-xs font-medium border ${getConditionBadge(product.condition)}`}>
                      {product.condition}
                    </div>
                  </div>
                  
                  {/* Rating */}
                  <div className="flex items-center mb-2">
                    <div className="flex text-yellow-400">
                      {'‚òÖ'.repeat(4)}{'‚òÜ'.repeat(1)}
                    </div>
                    <span className="text-sm text-gray-500 ml-1">({20 + Math.floor(Math.random() * 80)})</span>
                  </div>
                  
                  {/* Seller Info */}
                  <div className="flex items-center justify-between text-sm text-gray-500 mb-3">
                    <span>by {product.seller.username}</span>
                    <span>üìç {product.location}</span>
                  </div>
                  
                  {/* Category Badge */}
                  {product.category && (
                    <div className="mb-3">
                      <span className="inline-block bg-gray-100 text-gray-700 text-xs px-2 py-1 rounded-full">
                        {product.category.name}
                      </span>
                    </div>
                  )}
                  
                  {/* Action Buttons */}
                  <div className="flex space-x-2">
                    <button className="flex-1 bg-eco-green hover:bg-eco-dark text-white py-2 px-4 rounded-lg text-sm font-medium transition-colors">
                      Buy Now
                    </button>
                    <button className="px-4 py-2 border border-gray-300 rounded-lg text-sm font-medium text-gray-700 hover:bg-gray-50 transition-colors">
                      üí¨
                    </button>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default ProductList;
