import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './pages/Home';
import Login from './pages/Login';
import Signup from './pages/Signup';
import ScrollToTop from './components/ScrollToTop';
import { authAPI } from './api';
import './animations.css';

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  console.log('App component loaded');

  useEffect(() => {
    console.log('App useEffect triggered');
    const token = localStorage.getItem('access_token');
    if (token) {
      console.log('Token found, getting profile');
      authAPI.getProfile()
        .then(response => {
          console.log('Profile response:', response.data);
          setUser(response.data);
        })
        .catch((error) => {
          console.log('Profile error:', error);
          localStorage.removeItem('access_token');
          localStorage.removeItem('refresh_token');
        })
        .finally(() => {
          console.log('Setting loading to false');
          setLoading(false);
        });
    } else {
      console.log('No token found');
      setLoading(false);
    }
  }, []);

  const handleLogin = (userData, tokens) => {
    setUser(userData);
    localStorage.setItem('access_token', tokens.access);
    localStorage.setItem('refresh_token', tokens.refresh);
  };

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('access_token');
    localStorage.removeItem('refresh_token');
  };

  if (loading) {
    console.log('Showing loading screen');
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <div className="text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-green-500 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading Ecofinds...</p>
        </div>
      </div>
    );
  }

  console.log('Rendering main app, user:', user);

  // Simple test render first
  return (
    <div className="min-h-screen bg-gray-50">
      <div className="p-8">
        <h1 className="text-4xl font-bold text-green-600 mb-4">Ecofinds Test</h1>
        <p className="text-gray-700">If you can see this, React is working!</p>
        <p className="text-gray-700">User: {user ? user.username : 'Not logged in'}</p>
        <div className="mt-4">
          <button className="bg-green-500 text-white px-4 py-2 rounded">Test Button</button>
        </div>
      </div>
    </div>
  );
}

export default App;
