import React from 'react';

const CategoryFilter = ({ categories, selectedCategory, onCategoryChange }) => {
  const categoryIcons = {
    'Electronics': 'üì±',
    'Clothing & Fashion': 'üëó',
    'Home & Garden': 'üè†',
    'Books & Media': 'üìö',
    'Sports & Outdoors': '‚öΩ',
    'Vehicles': 'üöó',
    'Automotive': 'üîß',
    'Art & Collectibles': 'üé®',
    'Musical Instruments': 'üé∏',
    'Health & Beauty': 'üíÑ',
    'Toys & Games': 'üéÆ',
    'Jewelry & Watches': 'üíé'
  };

  return (
    <div className="mb-6">
      <h3 className="text-lg font-semibold text-gray-800 mb-4">Browse Categories</h3>
      <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-3">
        <button
          onClick={() => onCategoryChange('')}
          className={`p-4 rounded-lg border transition-all duration-200 ${
            selectedCategory === '' 
              ? 'bg-eco-green text-white border-eco-green shadow-md' 
              : 'bg-white text-gray-700 border-gray-200 hover:border-eco-green hover:shadow-md'
          }`}
        >
          <div className="text-2xl mb-2">üåç</div>
          <div className="text-sm font-medium">All Categories</div>
        </button>
        
        {categories.map(category => (
          <button
            key={category.id}
            onClick={() => onCategoryChange(category.id)}
            className={`p-4 rounded-lg border transition-all duration-200 ${
              selectedCategory === category.id.toString() 
                ? 'bg-eco-green text-white border-eco-green shadow-md' 
                : 'bg-white text-gray-700 border-gray-200 hover:border-eco-green hover:shadow-md'
            }`}
          >
            <div className="text-2xl mb-2">
              {categoryIcons[category.name] || 'üì¶'}
            </div>
            <div className="text-sm font-medium text-center">
              {category.name}
            </div>
          </button>
        ))}
      </div>
    </div>
  );
};

export default CategoryFilter;
