import React, { useState } from 'react';
import ProductList from '../components/ProductList';
import ProductForm from '../components/ProductForm';

const Home = ({ user }) => {
  const [showProductForm, setShowProductForm] = useState(false);
  const [refreshProducts, setRefreshProducts] = useState(0);
  const [heroSearchTerm, setHeroSearchTerm] = useState('');

  const handleProductSuccess = () => {
    setShowProductForm(false);
    setRefreshProducts(prev => prev + 1); // Trigger product list refresh
  };

  const handleHeroSearch = (e) => {
    e.preventDefault();
    // Scroll to products section and pass search term
    const productsSection = document.getElementById('products-section');
    if (productsSection) {
      productsSection.scrollIntoView({ behavior: 'smooth' });
    }
    // The search term will be passed to ProductList component
    setRefreshProducts(prev => prev + 1);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      {/* Hero Section */}
      <div className="bg-gradient-to-r from-eco-green to-eco-dark text-white py-16 rounded-xl mb-8 shadow-xl">
        <div className="text-center px-6">
          <h1 className="text-4xl md:text-6xl font-bold mb-4">Welcome to Ecofinds</h1>
          <p className="text-xl md:text-2xl mb-8 text-green-100">
            Discover sustainable secondhand treasures and give pre-loved items a new life
          </p>
          
          {/* Search Bar */}
          <div className="max-w-2xl mx-auto mb-8">
            <form onSubmit={handleHeroSearch} className="bg-white p-2 rounded-full shadow-lg">
              <div className="flex items-center">
                <input
                  type="text"
                  placeholder="Search for products..."
                  value={heroSearchTerm}
                  onChange={(e) => setHeroSearchTerm(e.target.value)}
                  className="flex-1 px-6 py-3 text-gray-700 bg-transparent border-none outline-none"
                />
                <button 
                  type="submit"
                  className="bg-eco-green hover:bg-eco-dark text-white px-6 py-3 rounded-full transition-colors"
                >
                  🔍 Search
                </button>
              </div>
            </form>
          </div>
          
          {/* Action Buttons */}
          <div className="flex flex-col sm:flex-row gap-4 justify-center items-center">
            {user && (
              <button
                onClick={() => setShowProductForm(!showProductForm)}
                className="bg-white text-eco-green px-8 py-3 rounded-lg font-semibold hover:bg-gray-100 transition-colors shadow-lg"
              >
                {showProductForm ? '❌ Cancel Posting' : '➕ Post a Product'}
              </button>
            )}
            {!user && (
              <div className="flex gap-4">
                <a 
                  href="/login" 
                  className="bg-white text-eco-green px-8 py-3 rounded-lg font-semibold hover:bg-gray-100 transition-colors shadow-lg"
                >
                  🔐 Login to Post
                </a>
                <a 
                  href="/signup" 
                  className="border-2 border-white text-white px-8 py-3 rounded-lg font-semibold hover:bg-white hover:text-eco-green transition-colors"
                >
                  🆕 Join Now
                </a>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Product Form */}
      {showProductForm && user && (
        <div className="mb-8">
          <ProductForm
            onSuccess={handleProductSuccess}
            onCancel={() => setShowProductForm(false)}
          />
        </div>
      )}

      {/* Stats Section */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
        <div className="bg-white p-6 rounded-xl shadow-lg text-center hover:shadow-xl transition-shadow">
          <div className="text-4xl mb-3">🌱</div>
          <div className="text-2xl font-bold text-eco-green mb-1">10K+</div>
          <h3 className="text-lg font-semibold text-gray-800 mb-1">Eco Warriors</h3>
          <p className="text-gray-600 text-sm">Committed to sustainability</p>
        </div>
        <div className="bg-white p-6 rounded-xl shadow-lg text-center hover:shadow-xl transition-shadow">
          <div className="text-4xl mb-3">💰</div>
          <div className="text-2xl font-bold text-eco-green mb-1">$2M+</div>
          <h3 className="text-lg font-semibold text-gray-800 mb-1">Money Saved</h3>
          <p className="text-gray-600 text-sm">By our community</p>
        </div>
        <div className="bg-white p-6 rounded-xl shadow-lg text-center hover:shadow-xl transition-shadow">
          <div className="text-4xl mb-3">🤝</div>
          <div className="text-2xl font-bold text-eco-green mb-1">50K+</div>
          <h3 className="text-lg font-semibold text-gray-800 mb-1">Happy Trades</h3>
          <p className="text-gray-600 text-sm">Successful transactions</p>
        </div>
        <div className="bg-white p-6 rounded-xl shadow-lg text-center hover:shadow-xl transition-shadow">
          <div className="text-4xl mb-3">♻️</div>
          <div className="text-2xl font-bold text-eco-green mb-1">95%</div>
          <h3 className="text-lg font-semibold text-gray-800 mb-1">Waste Reduced</h3>
          <p className="text-gray-600 text-sm">Environmental impact</p>
        </div>
      </div>

      {/* Products Section */}
      <div id="products-section" className="bg-white rounded-xl shadow-lg p-6">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-3xl font-bold text-gray-800">Latest Products</h2>
          <div className="flex items-center space-x-2 text-sm text-gray-500">
            <span>🔥</span>
            <span>Trending items in your area</span>
          </div>
        </div>
        <ProductList user={user} key={refreshProducts} heroSearch={heroSearchTerm} />
      </div>
      </div>
    </div>
  );
};

export default Home;
