import React, { useState } from 'react';
import ProductList from '../components/ProductList';
import ProductForm from '../components/ProductForm';

const Home = ({ user }) => {
  const [showProductForm, setShowProductForm] = useState(false);
  const [refreshProducts, setRefreshProducts] = useState(0);

  const handleProductSuccess = () => {
    setShowProductForm(false);
    setRefreshProducts(prev => prev + 1); // Trigger product list refresh
  };

  return (
    <div>
      {/* Hero Section */}
      <div className="bg-gradient-to-r from-eco-green to-eco-dark text-white py-16 rounded-xl mb-8 shadow-xl">
        <div className="text-center px-6">
          <h1 className="text-4xl md:text-6xl font-bold mb-4">Welcome to Ecofinds</h1>
          <p className="text-xl md:text-2xl mb-8 text-green-100">
            Discover sustainable secondhand treasures and give pre-loved items a new life
          </p>
          
          {/* Search Bar */}
          <div className="max-w-2xl mx-auto mb-8">
            <div className="bg-white p-2 rounded-full shadow-lg">
              <div className="flex items-center">
                <input
                  type="text"
                  placeholder="Search for products..."
                  className="flex-1 px-6 py-3 text-gray-700 bg-transparent border-none outline-none"
                />
                <button className="bg-eco-green hover:bg-eco-dark text-white px-6 py-3 rounded-full transition-colors">
                  üîç Search
                </button>
              </div>
            </div>
          </div>
          
          {/* Action Buttons */}
          <div className="flex flex-col sm:flex-row gap-4 justify-center items-center">
            {user && (
              <button
                onClick={() => setShowProductForm(!showProductForm)}
                className="bg-white text-eco-green px-8 py-3 rounded-lg font-semibold hover:bg-gray-100 transition-colors shadow-lg"
              >
                {showProductForm ? '‚ùå Cancel Posting' : '‚ûï Post a Product'}
              </button>
            )}
            {!user && (
              <div className="flex gap-4">
                <a 
                  href="/login" 
                  className="bg-white text-eco-green px-8 py-3 rounded-lg font-semibold hover:bg-gray-100 transition-colors shadow-lg"
                >
                  üîê Login to Post
                </a>
                <a 
                  href="/signup" 
                  className="border-2 border-white text-white px-8 py-3 rounded-lg font-semibold hover:bg-white hover:text-eco-green transition-colors"
                >
                  üÜï Join Now
                </a>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Product Form */}
      {showProductForm && user && (
        <div className="mb-8">
          <ProductForm
            onSuccess={handleProductSuccess}
            onCancel={() => setShowProductForm(false)}
          />
        </div>
      )}

      {/* Stats Section */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <div className="bg-white p-6 rounded-lg shadow text-center">
          <div className="text-3xl font-bold text-eco-green mb-2">üå±</div>
          <h3 className="text-lg font-semibold text-gray-800 mb-1">Sustainable</h3>
          <p className="text-gray-600">Reduce waste by buying secondhand</p>
        </div>
        <div className="bg-white p-6 rounded-lg shadow text-center">
          <div className="text-3xl font-bold text-eco-green mb-2">üí∞</div>
          <h3 className="text-lg font-semibold text-gray-800 mb-1">Affordable</h3>
          <p className="text-gray-600">Find great deals on quality items</p>
        </div>
        <div className="bg-white p-6 rounded-lg shadow text-center">
          <div className="text-3xl font-bold text-eco-green mb-2">ü§ù</div>
          <h3 className="text-lg font-semibold text-gray-800 mb-1">Community</h3>
          <p className="text-gray-600">Connect with local sellers and buyers</p>
        </div>
      </div>

      {/* Products Section */}
      <div>
        <h2 className="text-2xl font-bold text-gray-800 mb-6">Latest Products</h2>
        <ProductList user={user} key={refreshProducts} />
      </div>
    </div>
  );
};

export default Home;
